// initialise firebase and connects to hte backend for us
import { initializeApp } from 'firebase/app';
import { getFirestore } from 'firebase/firestore';
import { getStorage } from 'firebase/storage';

const firebaseConfig = {
  apiKey: "AIzaSyBcxyBTYfpXMBsG7ChMRwlgzUuwNtCXVlc",
  authDomain: "firegram-c6f39.firebaseapp.com",
  projectId: "firegram-c6f39",
  storageBucket: "firegram-c6f39.appspot.com",
  messagingSenderId: "374738019881",
  appId: "1:374738019881:web:f212048772a1c0426ede38"
}

initializeApp(firebaseConfig);
const projectStorage = getStorage();
const projectFirestore = getFirestore();
const timestamp = firebase.firestore.FieldValue.serverTimestamp;
export { projectStorage, projectFirestore, timestamp };
