// this useEffect HOOK will be used to handle file uploads and return useful info about that upload, progress etc
import {useState, useEffect} from 'react';
import { projectStorage, projectFirestore } from '../firebase/config';
import { ref, uploadBytesResumable, getDownloadURL } from 'firebase/storage';

const useStorage = (file) => {
    // progress of teh upload
    const [progress, setProgress] = useState(0);
    const [error, setError] = useState(null);
    // image url we get back from storage after the full upload
    const [url, setUrl] = useState(null);
    // the funciton inside useEffect will fire everytime the file dependancy changes i.e a new file
    useEffect(() => {
        // create reference
        // console.log(projectStorage);
        const storageRef = ref(projectStorage, file.name);
        // console.log(storageRef);
        const collection = projectFirestore.collection()

        const uploadTask = uploadBytesResumable(storageRef, file);

        uploadTask.on(
            'state_changed',
            (snapshot) => {
                let percentage =
                    (snapshot.bytesTransferred / snapshot.totalBytes) * 100;
                setProgress(percentage);
            },
            (err) => {
                setError(err);
            },
            () => {
                getDownloadURL(uploadTask.snapshot.ref).then((url) =>
                    setUrl(url)
                );
            }
        );
    }, [file]);
    // returning all these values here
    return { progress, url, error }
}

export default useStorage;
