// initialise firebase and connects to hte backend for us
import * as firebase from 'firebase/app';
import 'firebase/storage';
import 'firebase/firestore';



// Web App's Firebase config
const firebaseConfig = {
    apiKey: "AIzaSyBcxyBTYfpXMBsG7ChMRwlgzUuwNtCXVlc",
    authDomain: "firegram-c6f39.firebaseapp.com",
    projectId: "firegram-c6f39",
    storageBucket: "firegram-c6f39.appspot.com",
    messagingSenderId: "374738019881",
    appId: "1:374738019881:web:f212048772a1c0426ede38"
  };
// Initialize Firebase
firebaseConfig.initializeApp(firebaseConfig);
// this is used to interact with the storage service on firebase (backend)
// inits the services
const projectStorage = firebase.storage();
const projectFirestore = firebase.firestore();
// exports the services to use later
export { projectStorage, projectFirestore };
